sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isValidWhenNeededDate: function(whenNeededDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var whenNeeded = new GlideDateTime(whenNeededDate);

		if(whenNeeded.getDate() == rightnow.getDate() || whenNeeded.before(rightnow)){
			return false;
		}
		else{
			return true;
		}
	},

    type: 'NeedItUtils'
};
